import { useState } from "react";


const Form = (props) => {
    const handleInputChange = (event) => {
        const value = parseInt(event.target.value, 10); // Pobieramy wartość z inputa
        if (!isNaN(value)) {
            handleStepValueChange(value); // Przekazujemy wartość do komponentu `Steps`
        }
    };

    return (
        <div>
            <label>
                Enter Step Value:
                <input type="number" onChange={handleInputChange} defaultValue={1} />
            </label>
        </div>
    );
};


export default Form;
